// 增加 Quantumult X 导入

(function () {
  // 保存原始的 XMLHttpRequest 对象的引用
  var originalXHR = window.XMLHttpRequest;

  // 重写 XMLHttpRequest 构造函数
  function CustomXHR() {
    // 创建原始 XMLHttpRequest 对象
    var xhr = new originalXHR();

    // 拦截 open 方法
    var originalOpen = xhr.open;
    xhr.open = function (method, url, async) {
      // 在发送请求之前做些什么
      // 这里可以添加自定义的逻辑，例如判断 url 是否为目标接口

      // 调用原始的 open 方法
      originalOpen.apply(this, arguments);
    };

    // 拦截 onreadystatechange 事件
    xhr.onreadystatechange = function () {
      // 对响应数据做些什么
      if (xhr.readyState === 4 && xhr.status === 200) {
        // 判断响应的接口是否为目标接口
        if (xhr.responseURL.includes('/api/v1/user/getSubscribe')) {
          window.userInfo = JSON.parse(xhr.responseText).data;
        }
      }
    };

    return xhr;
  }

  // 将重写后的 XMLHttpRequest 对象替换为全局的 XMLHttpRequest
  window.XMLHttpRequest = CustomXHR;
})();

const classNameStr = "styles_mICH9";

